print("Welcome to Hangman")
var playGame = true

// Call top level function playGame

// playGame calls intro on the first call
// calls take turn
// calls print word
// calls playerOutcome when they lose or win
// playerOutcome calls playGame if user responds yes
//

while playGame {
    let randomWord = allTheWords.randomElement() ?? "happenstance"
    let maxGuesses = (randomWord.count / 2) + 2
    var strikes = 0
    let wordArray = Array(randomWord)
    var char : Character
    var blanks = Array(repeating: Character("_"), count: randomWord.count)
    var previousLetters = [Character:Bool]()
//    var previousLetters: Set<Character> = []
    let abcs = Set("abcdefghijklmnopqrstuvwxyz")
    print(abcs)
    var continueGame = true
    while continueGame &&  strikes !=  maxGuesses{
        print(blanks)
        print(randomWord)
        print("Please select a letter from A-Z")
        let userChoice = readLine()!
        // empty string has count zero
        guard userChoice.count == 1 else {
            print("Please enter a single letter which has not been selected")
            continue
        }
        
        char = Character(userChoice)
        guard abcs.contains(char) else {
            print("Please enter a unique letter from A-Z")
            continue
        }
        
        // I need to check in they entered the letter already.
        
        // The guard guarantees the condition is true for the rest of the
        // scope i.e. requires
        guard previousLetters[char] == nil else {
            print("You've already selected \(char)")
            continue
        }
        
        if wordArray.contains(char){
            for (idx, val) in wordArray.enumerated() where val == char {
                // update the blanks array
                blanks[idx] = char
            }
        } else {
            print("\(char) is not in the word! Please try again! ")
            strikes += 1
            print("Strikes left \(maxGuesses - strikes)")
        }
        previousLetters[char] = true
        if !blanks.contains("_"){
            print("You Win and had \(maxGuesses - strikes) strikes left.")
            continueGame = !continueGame
        }
    }
    if strikes == maxGuesses {
        print("You lose! The correct word was \(randomWord)")
    }
    print("Would you like to play again? (yes) (no)")
    let continuePlaying = readLine()!
    guard continuePlaying.lowercased() == "yes" else {break}
    }
